import React, { useState } from "react";

const Watch = () => {
  let time = new Date().toLocaleTimeString();

  const [ctime, setTime] = useState(time);

  setInterval(() => {
    time = new Date().toLocaleTimeString();
    setTime(time);
  }, 1000);

  return (
      <>
          <div></div>
      <h2>Live Watch</h2>
      <h3>{ctime}</h3>
    </>
  );
};

export default Watch;
