import React, { useState } from "react";

const Watch = () => {
  const time = new Date().toLocaleTimeString();

  const [ctime, setTime] = useState(ctime);

  setInterval(() => {
    ctime = new Date().toLocaleTimeString();
    setTime(ctime);
  }, 1000);

  return (
    <>
      <h2>Live Watch</h2>
      <h3>{ctime}</h3>
    </>
  );
};

export default Watch;
